import React from "react"
import {increment,decrement,click} from "../actions"
import {connect} from "react-redux"

class App extends React.Component{
  render(){
    const props = this.props
    return(
      <React.Fragment>
        <div>{props.count.value}</div>
        <button onClick={props.increment}>+1</button>
        <button onClick={props.decrement}>-1</button>
        <h1>{props.title}</h1>
        <button onClick={props.click}>Click here!</button>
      </React.Fragment>
    )
  }
}

const mapStateToProps = state => ({ count: state.countreducer,
                                    title: state.readreducer.title})
const mapDispatchToProps = ({ increment, decrement, click})

export default connect(mapStateToProps,mapDispatchToProps)(App)
